import React, { Component } from "react";
import axios from "axios";

class Game extends Component {
  state = {
    person: null,
    loading: true,
  };

  componentDidMount() {
    const res = axios.get("https://api.randomuser.me/").then((res) => {
      this.setState({ person: res.data.name });
    });
  }

  render() {
    return <h1>{this.state.person}</h1>;
  }
}

export default Game;
