import React, { Component } from "react";
import gamepage from "../css/gamepage.css";
import Game from "./Game.jsx";
import axios from "axios";

class GamePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      info: [],
    };
  }
  componentDidMount() {
    axios
      .get("insert api")
      .then((res) => {
        this.setState({ info: res.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }
  handleAddToCart = (e) => {
    e.preventDefault();
  };

  handleFavorites = (e) => {
    e.preventDefault();
  };

  render() {
    return (
      <div className="game-page">
        <h1>{this.state.info.name}</h1>
        <div className="center-elem">
          <img src={this.state.info.imageURL} alt="game pic" />
        </div>

        <div className="product-table">
          <table>
            <tr>
              <th>Developer</th>
              <th>{this.state.info.developer}</th>
            </tr>
            <th>Rating</th>
            <th>{this.state.info.rating}</th>
            <tr>
              <th>Platform(s)</th>
              <th>{this.state.info.platform}</th>
            </tr>
            <tr>
              <th>Release</th>
              <th>{this.state.info.release}</th>
            </tr>
            <tr>
              <th>Price</th>
              <th>{this.state.info.price}</th>
            </tr>
          </table>
        </div>
        <div className="cartbttn">
          <div>
            <button type="button" onClick={this.handleFavorites}>
              Add to Favorites
            </button>
            <button type="button" onClick={this.handleAddToCart}>
              Add to Cart
            </button>
          </div>
        </div>
        <br />
        <br />
        <br />
        <div className="game-description">
          <h1>Game Description</h1>
          <p>{this.state.description}</p>
        </div>
      </div>
    );
  }
}

export default GamePage;
