import React, { Component } from "react";
import gamepage from "../css/gamepage.css";

class GamePage extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  handleAddToCart = (e) => {
    e.preventDefault();
  };

  handleFavorites = (e) => {
    e.preventDefault();
  };

  render() {
    return (
      <div className="game-page">
        <h1>{this.props.name}</h1>
        <div className="center-elem">
          <img src={this.props.imageURL} alt="game pic" />
        </div>

        <div className="product-table">
          <table>
            <tr>
              <th>Developer</th>
              <th>{this.props.developer}</th>
            </tr>
            <th>Rating</th>
            <th>{this.props.rating}</th>
            <tr>
              <th>Platform(s)</th>
              <th>{this.props.platform}</th>
            </tr>
            <tr>
              <th>Release</th>
              <th>{this.props.release}</th>
            </tr>
            <tr>
              <th>Price</th>
              <th>{this.props.price}</th>
            </tr>
          </table>
        </div>
        <div className="cartbttn">
          <div>
            <button type="button" onClick={this.handleFavorites}>
              Add to Favorites
            </button>
            <button type="button" onClick={this.handleAddToCart}>
              Add to Cart
            </button>
          </div>
        </div>
        <br />
        <br />
        <br />
        <div className="game-description">
          <h1>Game Description</h1>
          <p>{this.state.description}</p>
        </div>
      </div>
    );
  }
}

export default GamePage;
