import React, { Component } from "react";
import gamepage from "../css/gamepage.css";

class GamePage extends Component {
  state = {
    name: "Sekiro: Shadows Die Twice",
    developer: "From Software",
    rating: "9.5/10",
    platform: "PC, PS4, Xbox One",
    release: "March 22, 2019",
    price: "$59.99",
    videoURL: "",
    imageURL:
      "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350",

    description:
      "Sekiro: Shadows Die Twice is an action-adventure video game developed by FromSoftware and published by Activision. The game follows a shinobi known as Wolf as he attempts to take revenge on a samurai clan who attacked him and kidnapped his lord",
  };

  render() {
    return (
      <div className="game-page">
        <h1>{this.state.name}</h1>

        <img src={this.state.imageURL} alt="game pic" />
        <br />
        <br />
        <br />
        <table>
          <tr>
            <th>Developer</th>
            <th>{this.state.developer}</th>
            {/* <th>Platforms</th>
            <th>Release</th>
            <th>Price</th> */}
          </tr>
          <th>Rating</th>
          <th>{this.state.rating}</th>
        </table>
      </div>
    );
  }
}

export default GamePage;

