import React, { Component } from "react";
import gamepage from "../css/gamepage.css";
import Game from "./Game.jsx";
import axios from "axios";

class GamePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      info: { name: "haitham" },
    };
  }
  componentDidMount() {
    axios
      .get("insert api")
      .then((res) => {
        this.setState({ info: res.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }
  handleAddToCart = (e) => {
    e.preventDefault();
  };

  handleFavorites = (e) => {
    e.preventDefault();
  };

  render() {
    return (
      <div className="game-page">
        <Game details={this.info} />
      </div>
    );
  }
}

export default GamePage;

