import React, { Component } from "react";
import gamepage from "../css/gamepage.css";

class GamePage extends Component {
  state = {
    // name: "Sekiro: Shadows Die Twice",
    // developer: "From Software",
    // rating: "9.5/10",
    // platform: "PC, PS4, Xbox One",
    // release: "March 22, 2019",
    // price: "$59.99",
    // videoURL: "",
    // imageURL:
    //   "https://images.pexels.com/photos/20787/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350",
    // description:
    //   "Sekiro: Shadows Die Twice is an action-adventure video game developed by FromSoftware and published by Activision. The game follows a shinobi known as Wolf as he attempts to take revenge on a samurai clan who attacked him and kidnapped his lord",
  };

  handleAddToCart = (e) => {
    e.preventDefault();
  };

  handleFavorites = (e) => {
    e.preventDefault();
  };

  render() {
    return (
      <div className="game-page">
        <h1>{this.state.name}</h1>
        <div className="center-elem">
          <img src={this.props.imageURL} alt="game pic" />
        </div>

        <div className="product-table">
          <table>
            <tr>
              <th>Developer</th>
              <th>{this.props.developer}</th>
            </tr>
            <th>Rating</th>
            <th>{this.props.rating}</th>
            <tr>
              <th>Platform(s)</th>
              <th>{this.props.platform}</th>
            </tr>
            <tr>
              <th>Release</th>
              <th>{this.state.release}</th>
            </tr>
            <tr>
              <th>Price</th>
              <th>{this.state.price}</th>
            </tr>
          </table>
        </div>
        <div className="cartbttn">
          <div>
            <button type="button" onClick={this.handleFavorites}>
              Add to Favorites
            </button>
            <button type="button" onClick={this.handleAddToCart}>
              Add to Cart
            </button>
          </div>
        </div>
        <br />
        <br />
        <br />
        <div className="game-description">
          <h1>Game Description</h1>
          <p>{this.state.description}</p>
        </div>
      </div>
    );
  }
}

export default GamePage;
